import React, { Component } from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Moment from 'react-moment';
import 'moment-timezone';
import pastCheck from './timecheck';

class Admin extends Component
{
    constructor(props){
        super(props);
            this.state ={
                curr : new Date(),
                days :  [],
            }
    this.pickDate(this.state.curr)
    }
    
    //선택한 날짜의 일주일 달력 프린트
    pickDate(e){
        var today = new Date()   
        var year = e.getFullYear();
        var month = e.getMonth();
        var day = e.getDate()
        
        var myDate = new Date(year, month, day, 0 ,0 ,0, 0)
        this.setState({ curr : e })
        this.state.days = [];
        for (let i = 0; i < 7; i++) {
         var setKey = "dateKey" + i
            this.state.days.push({ date: new Date(myDate.setDate(myDate.getDate() - myDate.getDay() + i)), key: setKey }) //월요일)
          
        }
        if(pastCheck(today, e) == true){
            console.log("past!!")
            var editable_input = document.getElementById("editable_input") 
            editable_input.disabled = "disabled"
        }
    }
    

    render(){
      
      const listItems = this.state.days.map((day) =>
      <p  key={day.key} id = {day.key}>
      <Moment format="YYYY/MM/DD, dddd" >
          {day.date}
      </Moment>
      </p>
      )
      
        return(
            <div id="detailInfo" style = {{margin:0, }}>
              
                <DatePicker
                id = "datepicker"
                selected={this.state.curr}      
                onSelect = {e => this.pickDate(e)}
                onKeyDown = {e => e.nativeEvent.returnValue == false}
                value = {this.state.curr}
                /*
                onSelect={handleDateSelect} //when day is clicked
                onChange={handleDateChange} //only when value has changed
                */
                />
                <div id ="weekDays">
                {listItems}
                </div>
                <div style={{margin_left:"50px"}}> 
                
              </div>
              <div>
                <table>
                <tbody>
                    <tr>
                    <td><p >일자 : </p> </td>
                    <td><Moment format="YYYY/MM/DD, dddd" id = "PickedDate">
                        {this.state.curr}
                    </Moment> </td>
                    </tr>
                    <tr>
                    <td><p>제목 :</p> </td>
                    <td><input id = "editable_input"></input> </td>
                    </tr>
                    <tr>
                    <td><p>재생시간 :</p> </td>
                    <td><input id = "editable_input"></input> </td>
                    </tr>
                    <tr>
                    <td><p>위치(URL) :</p> </td>
                    <td><input id = "editable_input"></input> </td>
                    </tr>
                    <tr>
                    <td><p>소스위치(URL) :</p> </td>
                    <td><input id = "editable_input"></input> </td>
                    </tr>
                    <tr>
                    <td><p>상세설명 :</p> </td>
                    <td><input id = "editable_input"></input> </td>
                    </tr>
                    <tr>
                    <td><p>구분 :</p> </td>
                    <td><input id = "editable_input"></input> </td>
                    </tr>
                    <tr>
                    <td><p>그룹 :</p> </td>
                    <td><input disabled="disabled" id = "editable_input"></input> </td>
                    </tr>
                    </tbody>
                </table>
                </div>
                </div>
        )
    }
}

export default Admin;
