import React, { Component } from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Moment from 'react-moment';
import 'moment-timezone';


class Admin extends Component
{
    constructor(props){
        super(props);
            this.state ={
                curr : new Date(),
                days : [],
            }
    }
    componentDidMount(){
        console.log(this.state.curr);
    }
    pickDate(e){
        this.setState({ curr : e})
        for (let i = 0; i < 7; i++) {
            this.state.days.push(new Date(this.state.curr.setDate(this.state.curr.getDate() - this.state.curr.getDay() + i))) //월요일)
           
        }
        console.log(this.state.days)
    }


    render(){
      const days = [0, 1, 2, 3, 4, 5, 6]
      const listItems = this.state.days.map((number) =>
        <li key={number.toString()}>
            {number}
        </li>
      );
        return(
            
            <div>
                <h1>Hello React World!!</h1>
                {listItems}
                <Moment format="YYYY/MM/DD">
                    {this.state.curr}
                </Moment>
                <DatePicker
                id = "datepicker"
                onKeyDown = {e =>  e.preventDefault()}
                selected={this.state.curr}
                value = {this.state.curr}
                onSelect = {e => this.pickDate(e)}
                /*
                onSelect={handleDateSelect} //when day is clicked
                onChange={handleDateChange} //only when value has changed
                */
                />
                
                </div>
        )
    }
}

export default Admin;
