import React, { Component } from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Moment from 'react-moment';
import 'moment-timezone';
import pastCheck from './timecheck';

class Admin extends Component
{
    constructor(props){
        super(props);
            this.state ={
                curr : new Date(),
                days :  [],
            }
    }
    componentDidMount(){
        console.log(this.state.curr);
        
    }
    pickDate(e){
        var today = new Date()   
        var year = e.getFullYear();
        var month = e.getMonth();
        var day = e.getDate()
        var myDate = new Date(year, month, day, 0 ,0 ,0, 0)
        this.setState({ curr : e })
        this.state.days = [];
        for (let i = 0; i < 7; i++) {
         
            this.state.days.push(new Date(myDate.setDate(myDate.getDate() - myDate.getDay() + i))) //월요일)
          
        }
        if(pastCheck(today, e) == true){
            console.log("past!!")
        };
        /*
        this.state.curr = e;
        this.state.pick = e
        this.setState({ curr : e});
        
      
        console.log(e)
        console.log(this.state.pick) */
        console.log("this value is e" + e)
    }
  

    render(){
     
    console.log('hello')
      const listItems = this.state.days.map((number) =>
      <p>
      <Moment format="YYYY/MM/DD, dddd" key={number.toString()}>
          {number}
      </Moment>
      </p>
      )
      
        return(
            
            <div>
                <h1>Hello React World!!</h1>
                <DatePicker
                id = "datepicker"
                selected={this.state.curr}      
                onSelect = {e => this.pickDate(e)}
                value = {this.state.curr}
                /*
                onSelect={handleDateSelect} //when day is clicked
                onChange={handleDateChange} //only when value has changed
                */
                />
                <div id ="weekDays">
                {listItems}
                </div>
                
                </div>
        )
    }
}

export default Admin;
