import React, { Component } from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Moment from 'react-moment';
import 'moment-timezone';


class Admin extends Component
{
    constructor(props){
        super(props);
            this.state ={
                curr : new Date(),
                pick : new Date(),
                days :  [],
            }
    }
    componentDidMount(){
        console.log(this.state.curr);
        
    }
    pickDate(e){
        this.state.days = [];

        this.setState({ curr : e, pick : e});
        console.log(this.state.pick);
        for (let i = 0; i < 7; i++) {
         
            this.state.days.push(new Date(this.state.pick.setDate(this.state.pick.getDate() - this.state.pick.getDay() + i))) //월요일)
           console.log(this.state.pick)
        }
    }
  

    render(){
     
    
      const listItems = this.state.days.map((number) =>
      <p>
      <Moment format="YYYY/MM/DD, dddd" key={number.toString()}>
          {number}
      </Moment>
      </p>
      )
      
        return(
            
            <div>
                <h1>Hello React World!!</h1>
                <DatePicker
                id = "datepicker"
                selected={this.state.curr}
                value = {e => e}
                onSelect = {e => this.pickDate(e)}
             
                /*
                onSelect={handleDateSelect} //when day is clicked
                onChange={handleDateChange} //only when value has changed
                */
                />
                <div id ="weekDays">
                {listItems}
                </div>
                
                </div>
        )
    }
}

export default Admin;
